var TxtRotate=function(el,toRotate,period){this.toRotate=toRotate;this.el=el;this.loopNum=0;this.period=parseInt(period,10)||2000;this.txt='';this.tick();this.isDeleting=!1};TxtRotate.prototype.tick=function(){var i=this.loopNum%this.toRotate.length;var fullTxt=this.toRotate[i];if(this.isDeleting){this.txt=fullTxt.substring(0,this.txt.length-1)}else{this.txt=fullTxt.substring(0,this.txt.length+1)}this.el.innerHTML='<span class="wrap"><span style="color: transparent;">.</span>'+this.txt+'</span>';var delta=95-Math.random()*45;if(this.isDeleting){delta/=2}if(!this.isDeleting&&this.txt===fullTxt){delta=this.period;this.isDeleting=!0}else if(this.isDeleting&&this.txt===''){this.isDeleting=!1;this.loopNum+=1;delta=500}setTimeout(()=>{;this.tick()},delta)};window.onload=()=>{var elements=document.getElementsByClassName('txt-rotate');for(var i=0;i<elements.length;i+=1){var toRotate=elements[i].getAttribute('data-rotate');var period=elements[i].getAttribute('data-period');if(toRotate){return new TxtRotate(elements[i],JSON.parse(toRotate),period)}}}